print((lambda w,g:max([(lambda x,y,v:(t:=lambda t:next(d for d in range(1,w) if t(d)))(lambda d:(c:=x-d)<=0 or g[c,y]>=v)*t(lambda d:(c:=x+d)>=w or g[c,y]>=v)*t(lambda d:(c:=y-d)<=0 or g[x,c]>=v)*t(lambda d:(c:=y+d)>=w or g[x,c]>=v))(x,y,g[(x,y)]) for x in range(0,w) for y in range(0,w)]))(len((l:=open("input.txt").readlines()))-1,{(i,j):int(h) for j,m in enumerate(l) for i,h in enumerate(m[:-1])}))